    win = 0
    log = []
    turns = 0
    while win == 0:
        coup = select(local_map)
        coup = coup - 1
        if coup > 6 : coup = 6
        if coup < 0 : coup = 0
        log.append(coup)
        pos = place(1,coup,local_map)
        if test_pos(pos[0],pos[1],1,local_map): 
            win = 1 
            break

        if type == "PVP":
            coup = select(local_map)
            coup = coup - 1
            if coup > 6 : coup = 6
            if coup < 0 : coup = 0
        else:
            coup = int(ia.calcul(map_to_ia(local_map))*7)
            if coup == 7 : coup = 6
        log.append(coup)
        pos = place(2,coup,local_map)
        if test_pos(pos[0],pos[1],2,local_map) : win = 2

        turns = turns + 2

        if turns >= 42 :
            win = None
            break
    end = False
    ret = 7
    while end == False:
        afiche("map",[map])
        import ion
        while True :
            if ion.keydown(ion.KEY_OK):
                if ret > 7 : ret = 1
                while ion.keydown(ion.KEY_OK):
                    jsp = 1
                end = True
                break
    return [win,log]

